import React, {useContext, useState} from 'react';
import { Route, Link, BrowserRouter as Router, Switch } from 'react-router-dom'

import { ThemeContext, themes } from './contexts/ThemeContext';
import { UserContext, user } from './contexts/UserContext';

import ProfilePage from './pages/profile-page';
import InputPage from './pages/input-page';

import './App.css';



function App() {
  /*constructor() {
    super();

    this.handleSetUserName = (event) => {
      const { value } = event.target;
      this.setState(state => ({
        signedInUser: {
          user: {
            id: state.signedInUser.user.id,
            name: value
          },
          setUser: state.signedInUser.setUser,
          handleSetUserName: state.signedInUser.handleSetUserName
        }
      }));
    };    

    this.state = {
      signedInUser: {
        user: user,
        setUser: (value => this.setState({ user: value })),
        handleSetUserName: this.handleSetUserName
      }
    }
*/
    const themeContext = useContext(ThemeContext);
    const [theme, setTheme] = useState(themeContext);
  debugger;
  return (
    <ThemeContext.Provider value={theme}>
      <UserContext.Provider value={user}>
        <Router>
          <Layout />
        </Router>
      </UserContext.Provider>
    </ThemeContext.Provider>
  );
}

function Layout() {
  return (
    <ThemeContext.Consumer>
      {theme => (
        <div style={theme}>
          <Header />
          <Toolbar />
          <br />
          <Content />
        </div>
      )}
    </ThemeContext.Consumer>
  );
}

function Header() {
  return (
    <header>
      <h1 style={{ margin: "inherit" }}>
        Paul's Test App
      </h1>
    </header>
  )
}

function Toolbar() {
  return (
    <ThemeContext.Consumer>
      {theme => (
        <nav>
          <ul>
            <li>
              <Link to="/" style={theme}>Display Data</Link>
            </li>
            <li>
              <Link to="/input" style={theme}>Enter Data</Link>
            </li>
          </ul>
        </nav>
      )}
    </ThemeContext.Consumer>
  )
}

// A component may consume multiple contexts
function Content() {
  return (
    <div style={{ paddingLeft: "25px" }}>
      <Switch>
        <Route exact path="/" component={ProfilePage} />
        <Route path="/input" component={InputPage} />
      </Switch>
    </div>
  );
}


export default App;
