import React, { useContext, useReducer } from "react";
import { ThemeContext, themes } from '../contexts/ThemeContext';

function toggleThemeReducer(state, action) {
    switch (action.type) {
      case "toggle": {
        return {
          selectedTheme: state.theme.selectedTheme === themes.dark
            ? themes.light
            : themes.dark
        }
      }
      case "light": {
        return { selectedTheme: themes.light }
      }
      case "dark": {
        return { selectedTheme: themes.dark }
      }
      default: {
        throw new Error("Unhandled type: ${action.type}")
      }
    }
  }
  
  function toggleTheme({ reducer = (s, a) => a.changes } = {}) {
    const [{ selectedTheme }, dispatch] = useReducer(
      (state, action) => {
        const changes = toggleThemeReducer(state, action)
        return reducer(state, { ...action, changes })
      },
      { selectedTheme: "light" }
    )
    const toggle = () => dispatch({ type: "toggle" })
    const setLight = () => dispatch({ type: "light" })
    const setDark = () => dispatch({ type: "dark" })
    return { selectedTheme, toggle, setLight, setDark }
  }

function ThemeTogglerButton() {
    const themeContext = useContext(ThemeContext);
    const { selectedTheme } = themeContext;

    return (
        <section>
            <button
                onClick={toggleTheme}
                style={selectedTheme}>
                Toggle Theme
            </button>
        </section>
    );
}


export default ThemeTogglerButton;
