import React from 'react';
import { Route, Link, BrowserRouter as Router, Switch } from 'react-router-dom'

import { ThemeContext, themes } from './contexts/ThemeContext';
import { UserContext, user } from './contexts/UserContext';

import ProfilePage from './pages/profile-page';
import InputPage from './pages/input-page';

import './App.css';



class App extends React.Component {
  constructor() {
    super();

    this.setUser = (value) => {
      this.setState(state => ({
          theme: {
            signedInUser: value,
            setTheme: this.setUser
          }
      }));
    }

    this.setTheme = (value) => {
      this.setState(state => ({
          theme: {
            selectedTheme: value,
            setTheme: this.setTheme
          }
      }));
    }

    this.state = {
      theme: {
        selectedTheme: themes.light,
        setTheme: this.setTheme
      },
      signedInUser: {
        user: user,
        setUser: this.setUser
      }
    }
  }

  render() {
    // App component that provides initial context values
    return (
      <ThemeContext.Provider value={this.state.theme}>
        <UserContext.Provider value={this.state.signedInUser}>
          <Router>
            <Layout />
          </Router>
        </UserContext.Provider>
      </ThemeContext.Provider>
    );
  }
}

function Layout() {
  return (
    <ThemeContext.Consumer>
      {theme => (
        <div style={theme.selectedTheme}>
          <Header />
          <Toolbar />
          <br />
          <Content />
        </div>
      )}
    </ThemeContext.Consumer>
  );
}

function Header() {
  return (
    <header>
      <h1 style={{ margin: "inherit" }}>
        Paul's Test App
      </h1>
    </header>
  )
}

function Toolbar() {
  return (
    <ThemeContext.Consumer>
      {theme => (
        <nav>
          <ul>
            <li>
              <Link to="/" style={theme.selectedTheme}>Display Data</Link>
            </li>
            <li>
              <Link to="/input" style={theme.selectedTheme}>Enter Data</Link>
            </li>
          </ul>
        </nav>
      )}
    </ThemeContext.Consumer>
  )
}

// A component may consume multiple contexts
function Content() {
  return (
    <div style={{ paddingLeft: "25px" }}>
      <Switch>
        <Route exact path="/" component={ProfilePage} />
        <Route path="/input" component={InputPage} />
      </Switch>
    </div>
  );
}


export default App;
