import React, { useContext, useReducer, useEffect } from "react";
import { UserContext } from '../contexts/UserContext';

function setUserNameReducer(state, event) {
    return {
        id: state.id,
        name: event.target && event.target.value
    }
}

function useSetUserName({ reducer = (s, a) => a.changes } = {}) {
    const userContext = useContext(UserContext);

    const [signedInUser, dispatch] = useReducer(
        (state, event) => {
            const changes = setUserNameReducer(state, event);
            return reducer(state, { ...event, changes })
        },
        userContext.user
    );

    const setUserName = (event) => dispatch(event);

    return { signedInUser, setUserName }
}

function UserInput() {
    const userContext = useContext(UserContext);
    const { signedInUser, setUserName } = useSetUserName({
        reducer(signedInUser, value) {
            return value.changes;
        }
    });

    // Pass new state via context when selectedTheme has changed
    useEffect(() => {
        if (userContext.signedInUser !== signedInUser) {
            //userContext.setUser(userContext.signedInUser);
        }
    });


    return (
        <section>
            <strong>User Name: </strong>
            <input
                type="text"
                name="name"
                onChange={(x => {setUserName(x.target.value)})}
                value={userContext.user? userContext.user.name : ''} />
        </section>
    );
}


export default UserInput;
