import React from 'react';

export const themes = {
    light: {
        name: 'light',
        color: '#000000',
        background: '#eeeeee',
    },
    dark: {
        name: 'dark',
        color: '#ffffff',
        background: '#222222',
    },
};

export const ThemeContext = React.createContext({
    theme: themes.dark,
    toggleTheme: () => {console.log(this.getState())},
  });
