import React from 'react';
import { Route, Link, BrowserRouter as Router } from 'react-router-dom'

import { ThemeContext, themes } from './ThemeContext';
import { UserContext, user } from './UserContext';
import ThemeTogglerButton from './theme-toggler-button';
import UserInput from './user-input';
import ProfilePage from './profile-page';

import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.toggleTheme = () => {
      this.setState(state => ({
        theme: {
          selectedTheme: state.theme.selectedTheme === themes.dark
            ? themes.light
            : themes.dark,
          setTheme: state.theme.setTheme,
          toggleTheme: state.theme.toggleTheme
        }
      }));
    };

    this.handleSetUserName = (event) => {
      const { value } = event.target;
      this.setState(state => ({
        signedInUser: {
          user: {
            id: state.signedInUser.user.id,
            name: value
          },
          setUser: state.signedInUser.setUser,
          handleSetUserName: state.signedInUser.handleSetUserName
        }
      }));
    };

    this.state = {
      theme: {
        selectedTheme: themes.light,
        setTheme: (value => this.setState({ theme: value })),
        toggleTheme: this.toggleTheme
      },
      signedInUser: {
        user: user,
        setUser: (value => this.setState({ user: value })),
        handleSetUserName: this.handleSetUserName
      }
    }
  }

  render() {
    // App component that provides initial context values
    return (
      <ThemeContext.Provider value={this.state.theme}>
        <UserContext.Provider value={this.state.signedInUser}>
          <Router>
            <div>
              <ul>
                <li>
                  <Link to="/">Enter Data</Link>
                </li>
                <li>
                  <Link to="/users">Display Data</Link>
                </li>
              </ul>
              <Switch>
                <Route exact path="/" component={Layout} />
                <Route path="/users" component={ProfilePage} />
                <Route path="/contact" component={Contact} />
              </Switch>
            </div>
          </Router>
        </UserContext.Provider>
      </ThemeContext.Provider>
    );
  }
}

function Layout() {
  return (
    <ThemeContext.Consumer>
      {theme => (
        <div style={theme.selectedTheme}>
          <Toolbar />
          <br /><hr />
          <Content />
        </div>
      )}
    </ThemeContext.Consumer>
  );
}

function handleOnSubmit(event) {
  event.preventDefault();
}


function Toolbar(props) {
  return (
    <nav>
      <form onSubmit={handleOnSubmit}>
        <UserInput />
        <br />
        <ThemeTogglerButton />
      </form>
    </nav>
  )
}

// A component may consume multiple contexts
function Content() {
  return (
    <ProfilePage />
  );
}


export default App;
