import React from 'react';

import {themes} from './theme-context';
import ThemeTogglerButton from './theme-toggler-button';
import ProfilePage from './profile-page';

import './App.css';

// Theme context, default to light theme
export const ThemeContext = React.createContext('light');

// Signed-in user context
const user = {
  name: 'Guest',
}
export const UserContext = React.createContext(user);

class App extends React.Component {
  constructor(props) {
    super(props);


    this.toggleTheme = () => {
      this.setState(state => ({
        theme: {
          styles: state.theme.styles === themes.dark
            ? themes.light
            : themes.dark,
          toggleTheme: state.theme.toggleTheme,
          setTheme: state.theme.setTheme
        }
      }));
    };

    this.state = {
      theme: {
        theme: themes.light,
        setTheme: (value => this.setState({theme: value})),
        toggleTheme: this.toggleTheme
      },      
      signedInUser: user
    };
  }

  render() {
    // App component that provides initial context values
    return (
      <ThemeContext.Provider value={this.state.theme}>
        <UserContext.Provider value={this.state.signedInUser}>
          <Layout />
        </UserContext.Provider>
      </ThemeContext.Provider>
    );
  }
}

function Layout() {
    return (
    <div>
      <Sidebar />
      <Content />
    </div>
  );
}


function Sidebar(props) {
  return (
    <ThemeContext.Consumer>
      {theme => (
        <nav>
            <ThemeTogglerButton value={theme}/>
        </nav>
    )}
    </ThemeContext.Consumer>
  )
}

// A component may consume multiple contexts
function Content() {
  return (
    <ThemeContext.Consumer>
      {theme => (
        <UserContext.Consumer>
          {user => (
            <ProfilePage user={user} theme={theme} />
          )}
        </UserContext.Consumer>
      )}
    </ThemeContext.Consumer>
  );
}


export default App;
