import React from 'react';

import {ThemeContext, themes} from './ThemeContext';
import {UserContext, user} from './UserContext';
import ThemeTogglerButton from './theme-toggler-button';
import ProfilePage from './profile-page';

import './App.css';


class App extends React.Component {
  constructor() {
    super();

    this.toggleTheme = () => {
      this.setState(state => ({
        theme: {
          selectedTheme: state.theme.selectedTheme === themes.dark
            ? themes.light
            : themes.dark,
          toggleTheme: state.theme.toggleTheme,
          setTheme: state.theme.setTheme
        }
      }));
    };

    this.state = {
      theme: {
        selectedTheme: themes.light,
        setTheme: (value => this.setState({theme: value})),
        toggleTheme: this.toggleTheme
      },      
      signedInUser: {
        user: user,
        setUser: (value => this.setState({user: value})),
      }
    };
  }

  render() {
    // App component that provides initial context values
    return (
      <ThemeContext.Provider value={this.state.theme}>
        <UserContext.Provider value={this.state.signedInUser}>
          <Layout />
        </UserContext.Provider>
      </ThemeContext.Provider>
    );
    }
}

function Layout() {
    return (
    <div>
      <Sidebar />
      <Content />
    </div>
  );
}


function Sidebar(props) {
  return (
    <nav>
        <ThemeTogglerButton />
    </nav>
  )
}

// A component may consume multiple contexts
function Content() {
  return (
        <UserContext.Consumer>
          {user => (
            <ProfilePage user={user} />
          )}
        </UserContext.Consumer>
  );
}


export default App;
